mapper.select:
SELECT * FROM ug_article u where u.status is not NULL
and u.title like #{title}
ORDER BY u.createtime DESC

SELECT u.* FROM ug_article u where u.id not in ( select uc.articleid from ug_article_column_re uc where uc.columnid = #{columnid})
and u.title like #{title}
ORDER BY u.createtime DESC

SELECT * FROM ug_article_column u where 1 =1
and u.title like #{title}
ORDER BY u.createtime DESC
